CustomField.formula:
if(AND(OR(ISPICKVAL(Account_Approval_Status__c, "Pending for Approval"), 
										ISPICKVAL(Account_Approval_Status__c, "1st Approver Approved"),
										ISPICKVAL(Account_Approval_Status__c, "2nd Approver Approved"),
										ISPICKVAL(Account_Approval_Status__c, "3rd Approver Approved" ),
										NOT(ISBLANK(Change_to_be_owner_ID__c) )
									)
							,
							NOT(
											OR(ISPICKVAL(Account_Approval_Status__c, "Final Approved") ,
														ISPICKVAL(Account_Approval_Status__c, "Recalled" ),
														ISPICKVAL(Account_Approval_Status__c, "Rejected" )
													) 
							)
						)
			,TRUE,FALSE )
