ValidationRule.errorConditionFormula:
AND( ISNEW(),IF( CONTAINS(RecordType.Name, 'Local Exclusive'),NOT(ISPICKVAL(Parent.RTL_Campaign_Type__c,'Local Exclusive')), 
	IF(CONTAINS(RecordType.Name, 'Exclusive'),NOT(ISPICKVAL(Parent.RTL_Campaign_Type__c,'Exclusive')),
		IF(CONTAINS(RecordType.Name, 'Mass'), NOT(ISPICKVAL(Parent.RTL_Campaign_Type__c,'Mass')),
			IF(CONTAINS(RecordType.Name,'Research'), NOT(ISPICKVAL(Parent.RTL_Campaign_Type__c,'Research')),FALSE)
		)
	)
))
