CustomField.formula:
if(EntitlementId = "",
  IMAGE("/resource/1579170953000/Case_SLA_Status_No_SLA", "No SLA", 25,120),
  if(text(Status) = "Completed",
    IMAGE("/resource/1579170882000/Case_SLA_Status_Done", "Done!", 25,80),
    if(AND(IsClosed = true, SLA_Day__c > 0 ),
       if( ClosedDate > Commit_Date__c , 
        IMAGE("/resource/1579170984000/Case_SLA_Status_Overdue", "Red", 25,80),
        IMAGE("/resource/1579170922000/Case_SLA_Status_Green", "Green", 25,80)),
       if(IsClosed = true,
        IMAGE("/resource/1579170922000/Case_SLA_Status_Green", "Green", 25,80),
        if(NOW() > Commit_Date__c, 
      IMAGE("/resource/1579170984000/Case_SLA_Status_Overdue", "Red", 25,80),
            IMAGE("/resource/1579170922000/Case_SLA_Status_Green", "Green", 25,80))
      )
    )
  )
)
